xpath.script:
$(document).ready(function() {

  $(document).on('change',"#subcategory", function(){
    update_sub_category_user();
  });   

  function update_sub_category_user(){
    $.ajax({url: "/support/subcategories/fetch?subcategory=" + $("#subcategory").val(), success: function(result){
      $("#subcategoy_user").html(result);
    }});
  }
});
