div.script:
            $(document).ready(function() {

              function load_support_tickets() {
                var tickets = new Array();


                  tickets.push({
                    personName: "", 
                    subject: "",
                    category: "",
                    state: "",
                    createDate: "",
                    viewBtn: 'View'      
                  });


                return tickets;
              }

              function unique(arr, prop) {
                return arr.map(function(e) { return e[prop]; }).filter(function(e,i,a){
                    return i === a.indexOf(e);
                });
              }

              function getFormattedDate(){
                return ['
d-m-y', '
H:i'].join("");
              }

              var ticketData = load_support_tickets();

              $('#ticketgrid').FancyGrid({    
                width: 700,
                height: 400,
                data: ticketData,
                selModel: 'row',
                cellHeight: 75,
                cellWrapper: true,
                defaults: {
                  ellipsis: true,
                  flex: 1
                },
                columns: [{
                  index: 'personName',      
                  title: 'Заявитель',
                  type: 'string',
                  filter: {
                    header: true,
                    emptyText: 'Search'
                  }
                },{
                  index: 'subject',
                  title: 'Тема',
                  type: 'string',
                  flex: 2,
                  filter: {
                    header: true,
                    emptyText: 'Search'
                  }
                },{
                  index: 'category',
                  title: 'Категория',
                  type: 'combo',
                  multiSelect: true, 
                  itemCheckBox: true,
                  data: unique(ticketData, 'category'),
                  filter: {
                    header: true,
                    emptyText: 'Search'
                  }
                },{
                  index: 'state',
                  title: 'Состояние',
                  type: 'combo',
                  multiSelect: true, 
                  itemCheckBox: true,
                  data: unique(ticketData, 'state'),
                  filter: {
                    header: true,
                    emptyText: 'Search'
                  }
                },{
                  index: 'createDate',
                  title: 'Дата',
                  type: 'date',
                  sortable: true,
                  format: {
                    read: 'Y-m-d H:i:s',
                    write: getFormattedDate()
                  }
                },{
                  index: 'viewBtn',
                  cellAlign: 'center',
                  editable: false,
                  ellipsis: false,
                  width: 60
                }]
              });

            });

form.script:
$(document).ready(function() {

    update_sub_categories()


    $("#category").change(function() {
         update_sub_categories()
    });

    function update_sub_categories() {
        $.ajax({url: "/support/subcategories/fetch?category=" + $("#category").val(), success: function(result){
            $("#subcategoy_container").html(result);
            update_sub_category_fields();
        }});            
    }

    $(document).on('change',"#subcategory", function(){
        update_sub_category_fields();
    });               

    function update_sub_category_fields() {
        $.ajax({url: "/support/subcategories/field/fetch?subcategory=" + $("#subcategory").val(), success: function(result){
            $("#subcategoy_fields").html(result);
        }});               
    }

    $('#file').change(function(){
        if(this.files.length > ) {
            alert("You can only upload " +  + " files");
        }

        for (var x in this.files) {
            if (this.files[x].size / 1024 > ) {
                alert(this.files[x].name + " exceeds the " +  + "KB file size limit");
                $('#file').val('');
            }
        }

    });

 });
